mapper.select:
SELECT  FROM inf_news c
WHERE c.TYPE = #{type} AND c.STATUS = #{status} 
ORDER BY c.UPDATE_DATE DESC

		SELECT 

		from inf_news c  

			c.STATUS != 'delete'

				AND c.TYPE = #{type} 


				AND c.STATUS = #{status} 


				AND c.UPDATE_DATE >= #{startDate} 


				AND c.UPDATE_DATE <= #{endDate} 


				AND (
					c.TITLE LIKE "%"#{keywords}"%"
					or c.AUTHOR LIKE "%"#{keywords}"%"
					or c.SOURCE LIKE "%"#{keywords}"%"
				)


      	order by c.INSERT_DATE DESC

select count(0) as a from inf_news c

	c.STATUS != 'delete'

		AND c.TYPE = #{type} 


		AND c.STATUS = #{status} 


		AND c.UPDATE_DATE >= #{startDate} 


		AND c.UPDATE_DATE <= #{endDate} 


		AND (
			c.TITLE LIKE "%"#{keywords}"%"
			or c.AUTHOR LIKE "%"#{keywords}"%"
			or c.SOURCE LIKE "%"#{keywords}"%"
		)

mapper.update:
UPDATE inf_news SET
	STATUS = #{status},
	UPDATE_DATE = #{updateDate}
WHERE 
	ID = #{id}
